function handle_path(t){jQuery.ajax({type:"POST",url:"/package/image_path.json",data:{key:t.product_key,version:t.product_version,scope:t.scope},dataType:"text",success:function(e){"nil"==e||show_pinit_button(e,t)}})}function upload_file(t){canvas=document.getElementById(t.canvas_id),canvas&&t.pinit&&jQuery.ajax({type:"POST",url:"/package/upload_image.json",data:{image:document.getElementById(t.canvas_id).toDataURL(),key:t.product_key,version:t.product_version,scope:t.scope},dataType:"text",success:function(e){show_pinit_button(e,t)}})}function show_pinit_button(t,e){if(0!=e.pinit){if(0==e.resize&&e.data_length>e.data_border){canvas_container=document.getElementById(e.container_id);var n=document.createElement("IMG");n.src=t,n.style.width="600px",canvas_container.appendChild(n)}pin_button=document.getElementById("pinit"),pin_button.href="https://pinterest.com/pin/create/button/?url=https%3A%2F%2Fwww.versioneye.com/package/"+e.product_key+"/"+e.product_version+"&media="+t+"&description="+e.product_name+" : "+e.version+" : "+e.scope,pin_button.style.display="block"}}